// Fix for inputSchema validation issues
// Some tools have invalid schemas that need to be corrected
const fixToolSchema = (tool: any) => {
  if (tool.inputSchema && typeof tool.inputSchema.type !== 'string') {
    // Fix invalid type - should be 'object' for MCP tools
    tool.inputSchema = {
      type: 'object',
      properties: {}
    };
  }
  return tool;
};

// Apply fixes to toolDefinitionMap
for (const [name, tool] of toolDefinitionMap.entries()) {
  toolDefinitionMap.set(name, fixToolSchema(tool));
}
